var leftValue = msg.payload["/Medibox/leftLDR"];
var rightValue = msg.payload["/Medibox/rightLDR"];

// Compare the values to find the maximum
var maxValue;
var maxSensor;

if (leftValue > rightValue) {
    maxValue = leftValue;
    maxSensor = "LEFT";
} else {
    maxValue = rightValue;
    maxSensor = "RIGHT";
}

// Create a new message object to send the result
var maxMessage = {
    payload: {
        maxValue: maxValue,
        maxSensor: maxSensor
    }
};

// Return the message with the maximum value and the corresponding sensor
return maxMessage;

var maxValue = msg.payload.maxValue;

var newMsg = { payload: maxValue };

return newMsg;

var defaults = {
    A: { offset: 45, control_factor: 0.5 },
    B: { offset: 60, control_factor: 0.6 },
    C: { offset: 30, control_factor: 0.7 },
    Custom: { offset: 0, control_factor: 0 } // You can set custom defaults here
};

// Get the selected option from the payload
var selectedOption = msg.payload;

var msg = {
    payload: {
        offset: defaults[selectedOption].offset,
        control_factor: defaults[selectedOption].control_factor
    }
};

return msg;

var msg1, msg2, msg3;

if (msg.payload == true){
    msg1 = { payload: true };  // Enable Alarm 1
    msg2 = { payload: true };  // Enable Alarm 2
    msg3 = { payload: true };  // Enable Alarm 3
}else{
    msg1 = { payload: false };  // Enable Alarm 1
    msg2 = { payload: false };  // Enable Alarm 2
    msg3 = { payload: false };  // Enable Alarm 3
}

return [msg1, msg2, msg3];

if (msg.activate === true) {
    var combinedPayload = {
        sign: msg.sign,
        hours: msg.hours,
        mins: msg.mins
    };
    return { payload: combinedPayload };
} else {
    return null;  // No message sent if 'activate' is not true
}
